import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, NavDropdown } from 'react-bootstrap';
import logo from '../images/logo.png';
import styled from 'styled-components';

const Title = styled.div`
  padding:14px 5px 14px 0px;
`;

const Text = styled.span`
  vertical-align: sub;
  font-size: calc(10px + 2vmin);
`;

export default class NavBar extends Component {

  onLogout = (e) => {
    e.preventDefault();
    localStorage.removeItem('token');
    localStorage.setItem('loggedIn', false);
    window.location.href = '/';
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
            <nav class="navbar navbar-light bg-light">
              <a class="navbar-brand" href="#">
                  <Title>
                    <img src={logo} id="imgUTSLogo" class="imgUTSLogo" alt="UTS" style={{width: '100px'}}/>
                    <Text>Quantum </Text><Text><b>Computing </b></Text>
                  </Title>
              </a>
              <span class="navbar-text">
                <a href="/login" class="nav-link" onClick={this.onLogout}>
                  <img src='data:image/png;base64,' style={{width: '32px'}}/>
                </a>
              </span>
            </nav>
        </header>

      </div>
    );
  }
}
